import React, { useState, useEffect } from 'react';
import StickyNote from '../StickyNote/StickyNote';
import Folder from '../Folder/Folder';
import firebase from 'firebase/compat/app';
import 'firebase/compat/firestore';

function GroupPage(props) {
    const groupData = props.group;
    const [stickyNotes, setStickyNotes] = useState([]);

    useEffect(() => {
        const db = firebase.firestore();
        // Fetch sticky notes from Firestore
        const fetchStickyNotes = async () => {
            try {
                const snapshot = await db.collection('users').get();
                const allNotes = [];
                snapshot.forEach(doc => {
                    const notes = doc.data().stickyNotes;
                    allNotes.push(...notes);
                });
                setStickyNotes(allNotes);
            } catch (error) {
                console.error('Error fetching sticky notes:', error);
            }
        };
        fetchStickyNotes();
    }, []); // Run once on component mount

    const addStickyNote = (memberName, id) => {
        // Your implementation to add sticky note
    };

    const updateStickyNoteContent = (id, content, memberName) => {
        // Your implementation to update sticky note content
    };

    return (
        <div>
            <h2>Group Page</h2>
            <p>Group Name: {groupData.groupName}</p>
            <p>Group Members</p>
            {Array.isArray(groupData.members) && groupData.members.length > 0 ? (
                <ul style={{ listStyleType: 'none', padding: 0 }}>
                    {groupData.members.map((member, index) => {
                        let name = member.split('@');
                        return (
                            <div key={index} style={{ position: 'relative' }}>
                                <li
                                    style={{
                                        cursor: 'pointer',
                                        padding: '5px',
                                        border: '4px solid black',
                                        borderRadius: '5px',
                                        marginBottom: '10px',
                                        background: 'white'
                                    }}
                                    onClick={() => addStickyNote(name[0], Date.now().toString())}
                                >
                                    {name[0]}
                                    <Folder />
                                </li>
                                {stickyNotes.map(note => {
                                    if (note.attachedTo === name[0]) {
                                        return (
                                            <StickyNote
                                                key={note.id}
                                                id={note.id}
                                                content={note.content}
                                                onUpdateContent={(content) => updateStickyNoteContent(note.id, content, name[0])}
                                                style={{
                                                    position: 'absolute',
                                                    top: '100%',
                                                    left: 0,
                                                    zIndex: 1
                                                }}
                                            />
                                        );
                                    }
                                    return null;
                                })}
                            </div>
                        );
                    })}
                </ul>
            ) : (
                <p>No members found</p>
            )}
        </div>
    );
}

export default GroupPage;
