import { useState, useEffect } from 'react';
import firebase from 'firebase/compat/app'; // Import only the necessary Firebase modules
import 'firebase/compat/auth';
import 'firebase/compat/firestore';

function CreateGroup() {
    const [users, setUsers] = useState([]);
    const [selectedUsers, setSelectedUsers] = useState([]);
    const [groupName, setGroupName] = useState('');

    useEffect(() => {
        // Fetch users from Firestore when component mounts
        const fetchUsers = async () => {
            try {
                const usersCollection = await firebase.firestore().collection('users').get();
                const usersData = usersCollection.docs.map(doc => doc.data());
                setUsers(usersData);
            } catch (error) {
                console.error('Error fetching users: ', error);
            }
        };

        fetchUsers();
    }, []);

    const handleUserSelection = (user) => {
        // Toggle user selection
        setSelectedUsers(prevSelectedUsers => {
            const isUserSelected = prevSelectedUsers.some(selectedUser => selectedUser.id === user.id);
            if (isUserSelected) {
                return prevSelectedUsers.filter(selectedUser => selectedUser.id !== user.id);
            } else {
                return [...prevSelectedUsers, user];
            }
        });
    };

    const handleGroupNameChange = (event) => {
        setGroupName(event.target.value);
    };

    const createGroup = async () => {
        try {
            // Get current user
            const currentUser = firebase.auth().currentUser;

            // Create a new group document
            const groupRef = await firebase.firestore().collection('groups').add({
                groupName: groupName,
                members: selectedUsers.map(user => user.id),
                createdBy: currentUser.uid,
                createdAt: firebase.firestore.FieldValue.serverTimestamp()
            });

            console.log('Group created with ID:', groupRef.id);
        } catch (error) {
            console.error('Error creating group:', error);
        }
    };

    return (
        <div>
        <h2>Create New Group</h2>
        <input type="text" placeholder="Group Name" value={groupName} onChange={handleGroupNameChange} />
        <div>
            <h3>Available Users</h3>
            {users.map(user => (
                <div key={user.id} onClick={() => handleUserSelection(user)}>
                    <span style={{ cursor: 'pointer', padding: '5px', border: '1px solid black', borderRadius: '5px', margin: '5px', background: selectedUsers.some(selectedUser => selectedUser.id === user.id) ? 'lightblue' : 'white' }}>{user.name}</span>
                </div>
            ))}
        </div>
        <button className="cloud-button" onClick={createGroup}>Create Group</button>
    </div>
    );
}

export default CreateGroup;
