import React, { useState, useEffect } from 'react';
import StickyNote from '../StickyNote/StickyNote';
import Folder from '../Folder/Folder';

import 'firebase/database'; // Import Firebase Realtime Database module

function GroupPage(props) {
    const groupData = props.group;
    const [stickyNotes, setStickyNotes] = useState([]);
    const [selectedMember, setSelectedMember] = useState(null);

    // Initialize Firebase database
    useEffect(() => {
        const database = firebase.database();
        const currentUser = firebase.auth().currentUser; // Get the current authenticated user

        if (currentUser) {
            const userId = currentUser.uid;
            const userRef = database.ref(`Users/${userId}/stickyNotes`);

            // Fetch sticky notes for the current user from Firebase
            userRef.on('value', (snapshot) => {
                const notes = snapshot.val() || [];
                setStickyNotes(notes);
            });
        }
    }, []); // Run this effect only once when the component mounts

    // Function to add a sticky note attached to a member
    const addStickyNote = (memberName) => {
        const newStickyNote = {
            id: Date.now(),
            content: '',
            attachedTo: memberName
        };

        setStickyNotes(prevStickyNotes => [...prevStickyNotes, newStickyNote]);

        // Update the sticky notes in Firebase
        updateStickyNotesInFirebase([...stickyNotes, newStickyNote]);
    };

    // Function to update the content of a sticky note
    const updateStickyNoteContent = (id, content) => {
        const updatedStickyNotes = stickyNotes.map(note =>
            note.id === id ? { ...note, content: content } : note
        );

        setStickyNotes(updatedStickyNotes);

        // Update the sticky notes in Firebase
        updateStickyNotesInFirebase(updatedStickyNotes);
    };

    // Function to update the sticky notes in Firebase
    const updateStickyNotesInFirebase = (notes) => {
        const currentUser = firebase.auth().currentUser;

        if (currentUser) {
            const userId = currentUser.uid;
            const userRef = firebase.database().ref(`Users/${userId}/stickyNotes`);
            userRef.set(notes);
        }
    };

    // Function to handle viewing notes attached to a member
    const onViewNotes = (memberName) => {
        setSelectedMember(memberName);
    };

    return (
        <div>
            <h2>Group Page</h2>
            <p>Group Name: {groupData.groupName}</p>
            <p>Group Members</p>
            {Array.isArray(groupData.members) && groupData.members.length > 0 ? (
                <ul style={{ listStyleType: 'none', padding: 0 }}>
                    {groupData.members.map((member, index) => {
                        let name = member.split('@');
                        return (
                            <div key={index} style={{ position: 'relative' }}>
                                <li
                                    style={{
                                        cursor: 'pointer',
                                        padding: '5px',
                                        border: '4px solid black',
                                        borderRadius: '5px',
                                        marginBottom: '10px',
                                        background: 'white'
                                    }}
                                    onClick={() => addStickyNote(name[0])}
                                >
                                    {name[0]}
                                    <Folder
                                        memberName={name[0]}
                                        stickyNotes={stickyNotes}
                                        onViewNotes={onViewNotes}
                                    />
                                </li>
                            </div>
                        );
                    })}
                </ul>
            ) : (
                <p>No members found</p>
            )}

            {/* Display sticky notes if a member's notes are selected */}
            {selectedMember && (
                <div>
                    <h3>Sticky Notes for {selectedMember}</h3>
                    {stickyNotes.map(note => {
                        if (note.attachedTo === selectedMember) {
                            return (
                                <StickyNote
                                    key={note.id}
                                    id={note.id}
                                    content={note.content}
                                    onUpdateContent={updateStickyNoteContent}
                                />
                            );
                        }
                        return null;
                    })}
                </div>
            )}
        </div>
    );
}

export default GroupPage;
