import React, { useContext,useState } from 'react';
import './StickyNote.css';

import { AuthContext } from '../GroupOptions/AuthContext.js';
const StickyNote = ({ initialContent }) => {
  const {user} = useContext(AuthContext);
    const userEmail = user.email; // Assuming email is stored in user.email

// Extracting name from email
const USERname = userEmail.split('@')[0];

  console.log("initail content",currentUser)
  const [currentUser, setCurrentUser] = useState(initialContent);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setDragOffset({
      x: e.clientX - position.x,
      y: e.clientY - position.y
    });
  };

  const handleMouseMove = (e) => {
    if (isDragging) {
      setPosition({
        x: e.clientX - dragOffset.x,
        y: e.clientY - dragOffset.y
      });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  return (
    <div
      className="sticky-note"
      style={{ top: position.y, left: position.x }}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
    >
      <textarea
        value={currentUser}
        onChange={(e) => setCurrentUser(e.target.value)}
        placeholder="Type something..."
      />
    </div>
  );
};

export default StickyNote;
