import React, { useState, useEffect } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/firestore';

function JoinGroup() {
    const [groups, setGroups] = useState([]);

    useEffect(() => {
        // Fetch groups from Firestore when component mounts
        const fetchGroups = async () => {
            try {
                const groupsCollection = await firebase.firestore().collection('groups').get();
                const groupsData = groupsCollection.docs.map(doc => ({ id: doc.id, ...doc.data() }));
                debugger
                setGroups(groupsData);
console.log("here is groupsdata",groupsData)
            } catch (error) {
                console.error('Error fetching groups:', error);
            }
        };

        fetchGroups();
    }, []);

    return (
        <div>
            <h2>Join Group</h2>
            <ul>
                {groups.map(group => (
                    <li key={group.id}>
                        <span style={{ cursor: 'pointer', padding: '5px', border: '1px solid black', borderRadius: '5px', margin: '5px'}}> {group.groupName}</span>
                        {/* Add button or link to join group */}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default JoinGroup;
