import React, { useState } from 'react';
import StickyNote from '../StickyNote/StickyNote';

function GroupPage(props) {
    // Accessing the 'group' prop passed from the parent component
    const groupData = props.group;

    // State to manage sticky notes
    const [stickyNotes, setStickyNotes] = useState([]);
    // State to keep track of the number of clicks on each member
    const [clickCounts, setClickCounts] = useState({});

    // Function to add a sticky note attached to a member
    const addStickyNote = (memberName) => {
        // Create a new sticky note object
        const newStickyNote = {
            id: Date.now(), // Unique ID for the sticky note
            content: '',  // Initial content of the sticky note
            attachedTo: memberName // Member name to which the sticky note is attached
        };

        // Update the state to include the new sticky note
        setStickyNotes(prevStickyNotes => [...prevStickyNotes, newStickyNote]);

        // Increment the click count for the member
        setClickCounts(prevClickCounts => ({
            ...prevClickCounts,
            [memberName]: (prevClickCounts[memberName] || 0) + 1
        }));
    };

    // Function to update the content of a sticky note
    const updateStickyNoteContent = (id, content) => {
        // Find the sticky note by ID and update its content
        const updatedStickyNotes = stickyNotes.map(note =>
            note.id === id ? { ...note, content: content } : note
        );

        // Update the state with the modified sticky note
        setStickyNotes(updatedStickyNotes);
    };

    // Render the group data
    return (
        <div>
            <h2>Group Page</h2>
            <p>Group Name: {groupData.groupName}</p>
            <p>Group Members</p>
            {Array.isArray(groupData.members) && groupData.members.length > 0 ? (
                <ul style={{ listStyleType: 'none', padding: 0 }}>
                    {groupData.members.map((member, index) => {
                        let name = member.split('@');
                        return (
                            <div key={index} style={{ position: 'relative' }}>
                                <li
                                    style={{
                                        cursor: 'pointer',
                                        padding: '5px',
                                        border: '4px solid black',
                                        borderRadius: '5px',
                                        marginBottom: '10px',
                                        background: 'white'
                                    }}
                                    onClick={() => addStickyNote(name[0])} // Attach sticky note to the member
                                >
                                    {name[0]}
                                </li>
                                {/* Render sticky notes attached to this member */}
                                <div style={{ display: 'flex', alignItems: 'center' }}>
                                    {Array.from({ length: clickCounts[name[0]] || 0 }).map((_, i) => (
                                        <StickyNote
                                            key={i}
                                            id={`sticky_${i}`}
                                            content={''} // Initial content of the sticky note
                                            onUpdateContent={(content) => updateStickyNoteContent(`sticky_${i}`, content)}
                                            style={{ marginRight: '10px' }} // Adjust margin between sticky notes
                                        />
                                    ))}
                                </div>
                            </div>
                        );
                    })}
                </ul>
            ) : (
                <p>No members found</p>
            )}
        </div>
    );
}

export default GroupPage;
