// MyGroup.js
import React, { useState, useEffect } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/compat/firestore';
import showgroupimage from './showGroup.jpeg'

function MyGroup({ user }) {
    const [groups, setGroups] = useState([]);

    useEffect(() => {
        const fetchGroups = async () => {
            try {
                const groupsRef = firebase.firestore().collection('groups');
                const snapshot = await groupsRef.where('members', 'array-contains', user.email).get();
                const fetchedGroups = [];
                snapshot.forEach(doc => {
                    fetchedGroups.push({ id: doc.id, ...doc.data() });
                });
                setGroups(fetchedGroups);
            } catch (error) {
                console.error('Error fetching groups:', error);
            }
        };

        fetchGroups();
    }, [user.email]);

    return (
        <div    style={{
            backgroundImage: `url(${showgroupimage})`, // Set the background image
            backgroundSize: 'cover', // Cover the entire container
            backgroundPosition: 'center', // Center the background image
            minHeight: '100vh', // Make sure the container covers the entire viewport height
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center'
        }}>
            <h2>My Groups</h2>
            <ul>
                {groups.map(group => (
                    <li style={{ cursor: 'pointer', padding: '5px', border: '1px solid black', borderRadius: '5px', margin: '5px', background: 'white' }} key={group.id}>{group.groupName}</li>


                ))}
            </ul>


        </div>
    );
}

export default MyGroup;
